import { GraphConverter } from "../GraphConverter.js";
import { idCreator } from "@plait/core";
export const createImage = (startPoint, imageItem) => {
    const { width, height, url } = imageItem;
    const points = [
        startPoint,
        [startPoint[0] + width, startPoint[1] + height],
    ];
    const imageElement = {
        id: idCreator(),
        type: "image",
        points,
        url,
    };
    return imageElement;
};
export const graphImageConverter = new GraphConverter({
    converter: (graph) => {
        const { width, height } = graph;
        const imageElement = createImage([0, 0], {
            width,
            height,
            url: graph.dataURL,
        });
        return { elements: [imageElement] };
    },
});
